import { useState } from "react";

const ProgresWM = ({ users }) => {
  console.log(users.length);

  const [getWoman, setWoman] = useState(0);
  const [getMan, setMan] = useState(0);

  useEffect(() => {
    let counter = 0;
    users.map((x) => {
      if (x.gender === "female") counter++;
    });
    console.log(counter);

    if (counter < 25) setColor("#78c953");
    if (counter > 25 && counter < 40) setColor("yellow");
    if (counter > 40) setColor("red");
    if (counter > 50) setButton("none");
  }, [users]);

  return (
    <div className="WM_container">
      <div className="woman" style={{ widht: getWoman + "%" }}></div>
      <div className="man" style={{ widht: gerMan + "%" }}></div>
    </div>
  );
};

export default ProgresWM;
