import { useState, useRef, useEffect } from "react";
import FavMovie from "./favMovie";

const FavoritePage = ({ getFavorites, setShowFav, setFavorites }) => {
  function close() {
    setShowFav(false);
  }
  //   let anim = getFavorites.filter((x) => x.genre === "Animaciniai");
  //   console.log(anim.length);

  let anim = 0;
  let dram = 0;
  let veiks = 0;
  let horr = 0;

  function checking(item) {
    if (item.includes("Animaciniai")) anim++;
    if (item.includes("Dramos")) dram++;
    if (item.includes("Veiksmo")) veiks++;
    if (item.includes("Siaubo")) horr++;
  }

  getFavorites.map((x) => checking(x.genre));
  console.log(anim, dram, veiks, horr);

  return (
    <div className="favorite">
      <div className="statistika">
        <h2>Animation:</h2>
        <h2>Horror:</h2>
        <h2>Drama:</h2>
        <h2>Action:</h2>
      </div>
      <div className="box">
        {getFavorites.map((x, i) => (
          <FavMovie
            index={i}
            key={i}
            item={x}
            setFavorites={setFavorites}
            getFavorites={getFavorites}
          />
        ))}
        <button onClick={close}>X</button>
      </div>
    </div>
  );
};

export default FavoritePage;
