import React from "react";

import { useSelector, useDispatch } from "react-redux";
import { minusQuant, addQuant } from "../features/products";
import { updateCart } from "../features/products";

function CartProduct({ item, index }) {
  const dispatch = useDispatch();
  const cart = useSelector((state) => state.products.value.products);

  function add() {
    dispatch(addQuant(index));
  }
  function minus() {
    dispatch(minusQuant(index));
    if (item.quant === 1) {
      const newCart = cart.filter((x) => x.id !== item.id);
      return dispatch(updateCart(newCart));
    }
  }

  return (
    <div>
      <div className="cartProd">
        <img src={item.image} alt="" />
        <h2>{item.title}</h2>
        <h4>Price:{item.price}</h4>

        <div>{item.quant}</div>
        <div className="increase">
          <button onClick={add}>+</button>
          <button onClick={minus}>-</button>
        </div>

        <div>TotalPrice: {item.price * item.quant}</div>
      </div>
    </div>
  );
}

export default CartProduct;
