import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

import { useDispatch } from "react-redux";
import { addProduct } from "../features/products";

function SingleProductPage() {
  const nav = useNavigate();

  const dispatch = useDispatch();
  const { id } = useParams();
  const [getSinglePr, setSinglePr] = useState([]);
  useEffect(() => {
    fetch("https://fakestoreapi.com/products/" + id)
      .then((res) => res.json())
      .then((data) => {
        setSinglePr(data);
      });
  }, []);

  function addToCart() {
    const newProd = getSinglePr;
    dispatch(addProduct(newProd));
    nav("/");
  }

  return (
    <div className="productPage">
      <img src={getSinglePr.image} alt="" />
      <div className="productinfo">
        <div>Title{getSinglePr.title}</div>
        <div>Description:{getSinglePr.description}</div>
        <div>Price:{getSinglePr.price}</div>
        <button onClick={addToCart}>ADD to CART</button>
      </div>
    </div>
  );
}

export default SingleProductPage;
