import React from "react";

import { useContext } from "react";
import { useRef } from "react";
import mainContext from "../context/mainContext";

function Comp2() {
  const inImage = useRef();
  const inTitle = useRef();
  const { getImage, setImage } = useContext(mainContext);

  function addImage() {
    const inputas = {
      image: inImage.current.value,
      title: inTitle.current.value,
    };
    setImage([...getImage, inputas]);
    console.log(getImage);
  }

  return (
    <div className="comp2">
      <input ref={inImage} type="url" name="" id="" placeholder="image" />
      <input ref={inTitle} type="text" placeholder="title" />
      <button onClick={addImage}>Add</button>
    </div>
  );
}

export default Comp2;
