import "./App.css";
import Filter from "./components/filter";
import Add from "./components/add";
import Tasks from "./components/tasks";

import { useState, useEffect, useRef } from "react";

function App() {
  const [getTask, setTask] = useState([]);
  const [getFilter, setFilter] = useState([]);
  const [getfilterOn, setFilterOn] = useState(false);

  return (
    <div className="App">
      <div className="filtravimas">
        <Filter
          getTask={getTask}
          setTask={setTask}
          getFilter={getFilter}
          setFilter={setFilter}
        />
      </div>
      <div className="taskai">
        {getTask.map((x, i) => (
          <Tasks
            key={i}
            index={i}
            getTaskas={x}
            setTask={setTask}
            getTask={getfilterOn ? getFilter : getTask}
          />
        ))}
      </div>

      <div className="pridejimas">
        <Add getTask={getTask} setTask={setTask} />
      </div>
    </div>
  );
}

export default App;
