import React from "react";
import { useSelector, useDispatch } from "react-redux";

import { useContext } from "react";
import mainContext from "../context/mainContext";
import { useState } from "react";
import {
  changeSelected,
  setAllselectedBacktoNotSlected,
} from "../features/users";

function SingleUser({ box, index }) {
  const { setInput, selectedBox, setSelectedBox } = useContext(mainContext);
  const allList = useSelector((state) => state.users.value.reservations);
  const dispatch = useDispatch();

  const [reserv, setReserv] = useState(box.reserved);

  function activeBox() {
    allList.map((x, i) => {
      let box = {
        selected: false,
        index: index,
      };
      dispatch(changeSelected(box));
    });

    const selectedBox = {
      selected: true,
      index: index,
    };
    dispatch(changeSelected(selectedBox));
  }

  return (
    <div
      style={{ backgroundColor: box.selected ? " lightgrey" : "" }}
      onClick={activeBox}
      className="onebox"
    >
      {reserv && <h3>Reserved by:{box.whoreservet}</h3>}
    </div>
  );
}

export default SingleUser;
