import React from "react";
import SingleProduct from "../components/SingleProduct";
import { useSelector, useDispatch } from "react-redux";
import { changeName, changePicture } from "../features/users";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function IndexPage({ products }) {
  const { setCount } = useContext(mainContext);
  const prod2 = useSelector((state) => state.products.products);
  // const username = useSelector((state) => state.users.value[1].username);
  // const dispatch = useDispatch();
  // let inImage = useRef();

  // function changeUserName() {
  //   const newName = "Jonas";
  //   dispatch(changeName(newName));
  // }
  // function changeUserPicture() {
  //   const newPic = inImage.current.value;
  //   dispatch(changePicture(newPic));

  // }
  useEffect(() => {
    setCount(prod2.products.length);
  }, [prod2]);

  return (
    <div className="products">
      {products.map((x, i) => (
        <SingleProduct key={i} item={x} />
      ))}
    </div>
  );
}

export default IndexPage;
