import React from "react";

const ShowUser = ({ user }) => {
    console.log(user);

 let color;
 let border
 let round
    if (user.border) border = "5px solid black";
    user.gender === "male" ? color = "blue" : color = "pink"
    user.round ? round = "10px": round = "0"

    let bgColor = {
      backgroundColor: color,
      border: border,
      borderRadius: round,
    };
let str = user.name
let matches = str.match(/\b(\w)/g); 
let acronym = matches.join('');

  return (
    <div className="boxas" style={bgColor}>

      <h1>{user.name}</h1>
      <h1>{user.email}</h1>
      <a href="#">{user.website}</a>
      <h2>{user.company.catchPhrase.toUpperCase()}</h2>
      <h2>Initials:{acronym} </h2>
    </div>
  );
};

export default ShowUser;
