import "./App.css";

import { useState, useRef } from "react";

function App() {
  let inUsername = useRef();
  let inPhoto = useRef();

  const [getUsers, setUsers] = useState([]);

  function addMessage() {
    if (inText.current.value == "") return alert("ivestike message");

    const user = {
      username: inText.current.value,
      photo: inPhoto.current.value,
    };
    setUsers([...getUsers, user]);
    console.log(getUsers);
  }

  return (
    <div className="App">
      <div className="container">
        <div className="form">
          <input ref={inPhoto} type="url" placeholder="photo url" />
          <input ref={inUsername} type="text" placeholder="username" />
          <button onClick={addMessage}>SEND</button>
        </div>
        <div className="display">
          {getMessage.map((x) => (
            <div className="objectas">
              {x.photo ? <img src={x.photo} alt="" /> : null}

              <div>{x.text}</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
