import React from "react";
import { useState, useEffect } from "react";

function ProgresBaras({ counter }) {
  const [getWidth, setWidth] = useState(0);
  const [getColor, setColor] = useState("#78c953");
  console.log(counter);
  useEffect(() => {
    if (counter <= 50) {
      setWidth(counter * 14);
    }

    if (counter < 25) setColor("#78c953");
    if (counter > 25 && counter < 40) setColor("yellow");
    if (counter > 40) setColor("red");
  }, [counter]);
  return (
    <div className="progressBar">
      <div className="progressWrapper">
        <div
          style={{ width: getWidth + "px", backgroundColor: getColor }}
        ></div>
      </div>
    </div>
  );
}

export default ProgresBaras;
