import React from "react";
import { useRef } from "react";

function CompUser() {
  const inName = useRef();
  const inUrl = useRef();
  function saveUser() {
    const user = {
      img: inUrl.current.value,
      name: inName.current.value,
    };
    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(user),
      credentials: "include",
    };

    fetch("http://localhost:4000/addUserSession", options)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      });
  }
  return (
    <div>
      <input ref={inUrl} type="url" placeholder="photo url" />
      <input ref={inName} type="text" placeholder="Name" />
      <button onClick={saveUser}>Set</button>
    </div>
  );
}

export default CompUser;
