import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateArr } from "../features/users";

function SingleUser({ user, index }) {
  const useriai = useSelector((state) => state.users.value);
  const dispatch = useDispatch();

  function deletingObj() {
    let tarpinis = [...useriai];
    tarpinis = tarpinis.filter((x, i) => i !== index);

    dispatch(updateArr(tarpinis));
  }
  return (
    <div>
      <img src={user.userimage} alt="" />
      <div>{user.username}</div>
      <div>{user.usercity}</div>
      <button onClick={deletingObj}>DELETE</button>
    </div>
  );
}

export default SingleUser;
