import React from "react";
import { useSelector, useDispatch } from "react-redux";

import { useContext } from "react";
import mainContext from "../context/mainContext";
import { useState } from "react";
import { changeSelected } from "../features/users";

function SingleUser({ box, index }) {
  const { setInput, setCancel, setIndeksas } = useContext(mainContext);
  const allList = useSelector((state) => state.users.value.reservations);
  const dispatch = useDispatch();

  const [reserv, setReserv] = useState(box.reserved);

  function activeBox() {
    setInput(true);
    setCancel(false);
    allList.map((x, i) => {
      let box = {
        selected: false,
        index: i,
      };
      dispatch(changeSelected(box));
    });

    const selectedBox = {
      selected: true,
      index: index,
    };
    dispatch(changeSelected(selectedBox));
    setIndeksas(index);

    if (box.reserved) {
      setInput(false);
      setCancel(true);
    }
  }

  return (
    <div
      style={{
        backgroundColor: box.selected
          ? "lightgrey"
          : box.reserved
          ? "grey"
          : "",
      }}
      onClick={activeBox}
      className="onebox"
    >
      {/* <h3>Reserved by:{box.whoreservet}</h3> */}
      {box.reserved && <h3>Reserved by:{box.whoreservet}</h3>}
    </div>
  );
}

export default SingleUser;
