import React from "react";
import Hash from "../components/Hash";
import { useRef } from "react";

function GamePage({ word }) {
  const inLetter = useRef();
  function guessing() {
    console.log(inLetter.current.value);

    const guessing = {
      letter: inLetter.current.value,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(guessing),
    };

    fetch("http://localhost:4000/guessing", options)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      });

    inLetter.current.value = "";
  }
  return (
    <div className={"p-20 m-auto"}>
      <div className="all_hashes">
        {word.map((x, i) => (
          <Hash leter={x} key={i} />
        ))}
      </div>
      <div>
        <input
          ref={inLetter}
          type="text"
          name=""
          id=""
          placeholder="leter"
          maxLength="1"
        />
        <button onClick={guessing}>GUESS</button>
      </div>
    </div>
  );
}

export default GamePage;
