import React from "react";
import { useState, useRef } from "react";
import SinglePost from "../components/SinglePost";

function GamePage() {
  const [post, setPosts] = useState([]);
  const [images, setImages] = useState([]);
  const [ingredient, setIngredients] = useState([]);
  const [prepStep, setPrepSteps] = useState([]);
  const [error, setError] = useState(null);

  const image = useRef();
  const title = useRef();
  const ingredients = useRef();
  const prepSteps = useRef();
  const cost = useRef();

  function send() {
    const post = {
      images,
      title: title.current.value,
      ingredient,
      preparationSteps: prepStep,
      cost: cost.current.value,
    };

    const options = {
      method: "POST",
      headers: {
        "content-type": "application/json",
      },
      body: JSON.stringify(post),
    };

    fetch("http://localhost:4000/start", options)
      .then((res) => res.json())
      .then((data) => {
        if (data.error) return setError(data.messge);

        setPosts(data.posts);
        console.log(data);
      });
  }

  function addImage() {
    setImages([...images, image.current.value]);
    image.current.value = "";
  }
  function addIngredient() {
    setIngredients([...ingredient, ingredients.current.value]);
    ingredients.current.value = "";
  }
  function addPrepSteps() {
    setPrepSteps([...prepStep, prepSteps.current.value]);
    prepSteps.current.value = "";
  }

  return (
    <div>
      <div className="start">
        <input ref={image} type="text" placeholder="image" />
        <button onClick={addImage}>add image</button>

        <input ref={title} type="text" placeholder="title" />

        <input ref={ingredients} type="text" placeholder="ingridients" />
        <button onClick={addIngredient}>add ingridient</button>

        <input ref={prepSteps} type="text" placeholder="prepSteps" />
        <button onClick={addPrepSteps}>Add step</button>

        <input ref={cost} type="text" placeholder="Cost" />

        <button onClick={send}>Send</button>

        {error && <h4>{error}</h4>}
      </div>
      <div>
        {post.map((x, i) => (
          <SinglePost key={i} post={x} />
        ))}
      </div>
    </div>
  );
}

export default GamePage;
