import "./App.css";
import { useState, useRef } from "react";

function App() {
  let inpValue = useRef();

  function setingColor() {
    let spalva = inpValue.current.value;
    setColor(spalva);
  }

  const [color, setColor] = useState("white");

  let Color = {
    backgroundColor: color,
  };

  return (
    <div className="App">
      <div className="container" style={Color}>
        <input ref={inpValue} type="text" placeholder="color" />
        <button onClick={setingColor}>Set Color</button>
      </div>
    </div>
  );
}

export default App;
