import React from "react";
import { useRef } from "react";

function CreateUsers() {
  const inImage = useRef();
  const inTitle = useRef();
  const inLocation = useRef();
  const inUsername = useRef();
  const inDescription = useRef();
  const user = {
    name: "Andrius",
    age: 29,
    city: "Trakai",
  };
  const options = {
    method: "POST",
    headers: {
      "content-type": "application/json",
    },
    body: JSON.stringify(user),
  };

  function send() {
    fetch("http://localhost:4000/addUser ", options)
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
      });
  }
  return (
    <div>
      <input ref={inImage} type="url" placeholder="image" />
      <input ref={inTitle} type="text" placeholder="title" />
      <input ref={inLocation} type="text" placeholder="location" />
      <input ref={inUsername} type="text" placeholder="usermae" />
      <input ref={inDescription} type="text" placeholder="description" />
      <button onClick={send}>Send</button>
    </div>
  );
}

export default CreateUsers;
