import React from "react";
import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

function RegisterPage({ getUser, setUser }) {
  const nav = useNavigate();
  const [values, setValues] = useState({
    id: 1,
    email: "",
    pass1: "",
    pass2: "",
  });
  const [error, setError] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (error.includes(name)) {
      setError(error.filter((e) => e !== name));
    }
    setValues({ ...values, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { email, pass1, pass2 } = values; //can add rest according to your requirement
    const errorList = [];
    const regex =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;

    if (!email || regex.test(email) === false) {
      errorList.push("email");
    }
    if (!pass1 || pass1.length > 20 || pass1.length < 3) {
      errorList.push("pass1");
    }
    if (!pass2 || pass2 !== pass1) {
      errorList.push("pass2");
    }

    setError(errorList);

    console.log(errorList);

    // JEIGU VISKAS OK, preideda useri i getUser ir nukreipia i profilio page.
    if (errorList.length === 0) {
      console.log("tuscia");
      setUser([...getUser, values]);

      nav("/profile");
    }

    console.log(getUser);
  };
  // console.log(error);
  return (
    <div className="register">
      <input
        type="text"
        placeholder="email"
        name="email"
        value={values.email}
        onChange={handleInputChange}
        className={error.includes("email") ? "error" : ""}
      />
      <input
        type="text"
        placeholder="enter password"
        name="pass1"
        value={values.pass1}
        onChange={handleInputChange}
        className={error.includes("pass1") ? "error" : ""}
      />
      <input
        type="text"
        placeholder="confirm password"
        name="pass2"
        value={values.pass2}
        onChange={handleInputChange}
        className={error.includes("pass2") ? "error" : ""}
      />
      <button onClick={handleSubmit}>REGISTER</button>
    </div>
  );
}

export default RegisterPage;
