import React from "react";
import { useState } from "react";

function Coment({ addLike, x, i }) {
  const [getLike, setLike] = useState(x.likes);
  function setlikes() {
    setLike(getLike + 1);
  }
  return (
    <div className="comments" key={i}>
      {x.email} commented at: {x.time}
      <p>Comment: {x.text}</p>
      <i>Likes({getLike})</i>
      <i
        onClick={() => {
          setLike(), addLike(i);
        }}
        className="fa fa-thumbs-up"
      ></i>
    </div>
  );
}

export default Coment;
