import "./App.css";
import { useState, useRef } from "react";

function App() {
  let inpValue = useRef();

  function setingColor() {
    let color = inpValue.current.value;
    console.log(color);
  }

  const [color, setColor] = useState("white");

  let Color = {
    backgroundColor: color,
  };

  return (
    <div className="App" style={Color}>
      <input ref={inpValue} type="text" placeholder="color" />
      <button onClick={setingColor}>Set Color</button>
    </div>
  );
}

export default App;
