import React from "react";
import { useContext, useState } from "react";
import mainContext from "../context/mainContext";
import { useRef } from "react";
import Coment from "../components/Coment";

function SinglePostPage() {
  const { logedinUser, selectedPost, setSelectedPost, posts, setPosts } =
    useContext(mainContext);
  // console.log(selectedPost);
  const comm = selectedPost.coments;
  // console.log(comm);
  const inText = useRef();

  function addComment() {
    const item = {
      email: logedinUser.email,
      text: inText.current.value,
      time: Date(),
      likes: 0,
    };
    const allcomments = [...selectedPost.coments, item];
    const postas = { ...selectedPost, coments: allcomments };

    setSelectedPost(postas);

    //randa kurioje vietoje guli postas ir iraso vietoj jo.
    //randa index
    const postIndex = posts.findIndex((x) => x.id == postas.id);
    // sukuriam nauja areju su visais postasi
    const newAllposts = posts;
    // naujam arejuje i posto indexa ideda nauja posta.
    newAllposts[postIndex] = postas;
    //uzsetinam postus isnaujo.
    setPosts(newAllposts);
  }

  function addLike(index) {
    // console.log(selectedPost.coments[index].likes);
    let like = selectedPost.coments[index].likes;
    like++;

    const li = { ...selectedPost.coments[index], likes: like };
    const commmm = selectedPost.coments;

    commmm[index] = li;

    const newselsected = selectedPost;
    newselsected.coments = commmm;

    setSelectedPost(newselsected);
    console.log(selectedPost);
  }

  return (
    <div className="center">
      <div className="container">
        <div className="single-post">
          <img src={selectedPost.image} alt="" />
          <div className="info">
            <h1>Image title:{selectedPost.title}</h1>
            <h4>Location:{selectedPost.location}</h4>
            <h1>User:{logedinUser.email}</h1>
            <input ref={inText} type="text" placeholder="comment text" />
            <button onClick={addComment}>Add</button>
          </div>
        </div>

        {comm.map((x, i) => (
          <Coment addLike={addLike} key={i} i={i} x={x} />
        ))}
      </div>
    </div>
  );
}

export default SinglePostPage;
