import React from "react";
import { useParams } from "react-router-dom";
import { useRef, useState } from "react";

function ProfilePage({ logedinUser }) {
  const [getPicture, setPicture] = useState(logedinUser);
  let inPic = useRef();
  //   const { em } = useParams();
  //   const user = getUser.find(({ email }) => email === em);
  //   console.log(em);
  function changePicture() {
    const user = { ...getPicture };
    user.picture = inPic.current.value;

    setPicture(inPic.current.value);
    console.log(inPic.current.value);
  }
  return (
    <div className="user">
      <img src={getPicture} alt="" />
      <h1>{logedinUser.email}</h1>
      <input ref={inPic} type="url" />
      <button onClick={changePicture}> CHANGE PICTURE </button>
    </div>
  );
}

export default ProfilePage;
