import React from "react";
import Picture from "./Picture";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function Comp6() {
  const { getImage, mainBar, main } = useContext(mainContext);
  const styling = {
    flexDirection: mainBar ? "column" : "row",
    background: main.color,
  };
  return (
    <div className="comp6" style={styling}>
      {getImage.map((x) => (
        <Picture object={x} />
      ))}
    </div>
  );
}

export default Comp6;
