import "./App.css";

import { useState, useRef } from "react";
// import Images from "./components/Image";

function App() {
  let inRepeat = useRef();
  let inPhoto = useRef();

  const [getPhoto, setPhoto] = useState([]);

  function addMessage() {
    const image = {
      photo: inPhoto.current.value,
      repeat: inRepeat.current.value,
    };
    setPhoto([...getPhoto, image]);
  }

  return (
    <div className="App">
      <div className="container">
        <div className="form">
          <input ref={inUser} type="text" placeholder="username" />
          <input ref={inUser} type="email" placeholder="email" />
          <input ref={inUser} type="" placeholder="pasword" />
          <input ref={inUser} type="comfirm pasword" placeholder="" />
          <input
            className="message"
            ref={inRepeat}
            type="number"
            placeholder="count"
          />
          <button onClick={addMessage}>Repeat</button>
        </div>
      </div>
    </div>
  );
}

export default App;
