import React from "react";

import { useSelector, useDispatch } from "react-redux";
import { minusQuant, addQuant } from "../features/products";

function CartProduct({ item, index }) {
  const dispatch = useDispatch();
  const prod = useSelector((state) => state.products.value.products);

  function add() {
    dispatch(addQuant(index));
  }
  function minus() {
    dispatch(minusQuant(index));
  }

  return (
    <div>
      <div className="cartProd">
        <img src={item.image} alt="" />
        <div>{item.title}</div>
        <div>Price:{item.price}</div>
        <button onClick={add}>add</button>
        <div>{item.quant}</div>
        <button onClick={minus}>remove</button>
        <div>TotalPrice: {item.price * item.quant}</div>
      </div>
    </div>
  );
}

export default CartProduct;
