import "./App.css";
import { useState } from "react";
import Header from "./components/header";

function App() {




  const [getGold, setGold] = useState(0);
  const [getWood, setWood] = useState(0);
  const [getStone, setStone] = useState(0);

  function addOne(num){

    setGold(getGold+1)
  }





  return (
    <div className="App">
      < Header gold = {getGold} wood = {getWood} stone = {getStone}/>
      <div className="resourse">
        <img onClick={addOne} src="https://img.freepik.com/free-vector/pile-gold-bullion-with-pixel-art-style_475147-541.jpg?w=2000" alt="" />
        <img onClick={addOne} src="https://img.freepik.com/free-vector/wood-log-pixel-art-style_475147-1390.jpg" alt="" />
        <img  onClick={addOne} src="https://img.freepik.com/free-vector/sharp-stone-pixel-art-style_475147-1450.jpg" alt="" />
      </div>


    </div>
  );
}

export default App;
