import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateArr } from "../features/users";
import { useContext } from "react";
import mainContext from "../context/mainContext";
import { useState } from "react";
import { changeSelected } from "../features/users";

function SingleUser({ box, index }) {
  const { setInput, selectedBox, setSelectedBox } = useContext(mainContext);
  const useris = useSelector((state) => state.users.value.reservations);
  const dispatch = useDispatch();

  const [reserv, setReserv] = useState(box.reserved);

  function activeBox() {
    useris[index].selected = true;
    // const newValue = true;
    console.log(useris[index].selected);

    // dispatch(changeSelected(useris));
  }

  return (
    <div
      style={{ backgroundColor: box.selected ? " lightgrey" : "" }}
      onClick={activeBox}
      className="onebox"
    >
      {reserv && <h3>Reserved by:{box.whoreservet}</h3>}
    </div>
  );
}

export default SingleUser;
