import React from "react";
import { updateUsers } from "../features/users";
import { useSelector, useDispatch } from "react-redux";

function SingleUser({ item }) {
  const alluseriai = useSelector((state) => state.users.value.users);

  function deleting() {
    const newusers = alluseriai.filter((x) => x.time !== item.time);
    return dispatch(updateUsers(newusers));
  }
  return (
    <div className="single">
      <img src={item.photo} alt="" />
      <h1>{item.username}</h1>
      <p>{item.time}</p>
      <button onClick={deleting}>DELETE</button>
    </div>
  );
}

export default SingleUser;
