import { useState, useRef, useEffect } from "react";

const Add = ({ getTask, setTask }) => {
  let inText = useRef();
  function addTask() {
    const taskas = {
      value: inText.current.value,
      completed: false,
      date: "2022.01.01",
    };
    setTask([...getTask, taskas]);
    console.log(getTask);
  }

  return (
    <div className="add">
      <input ref={inText} type="text" placeholder="Add a TASK" />
      <button onClick={addTask}>ADD</button>
    </div>
  );
};

export default Add;
