import React from "react";

import { useContext } from "react";
import mainContext from "../context/mainContext";
import Inventor from "../components/Inventor";

function ArenaPage() {
  const { character, getInventor } = useContext(mainContext);
  console.log(character);
  return (
    <div className="arena">
      <div className="mainpage">
        <div className="player_info">
          <img src={character.image} alt="" />
          <div className="hpuser-wraper">
            <div className="hp-user"></div>
          </div>
          <div className="weapons">
            <img
              src="https://wow.gamepressure.com/gfx/icons/INV_Sword_04.gif"
              alt=""
            />
          </div>
        </div>
        <button className="fight">FIGHT</button>
        <div className="monster">
          <img src={character.image} alt="" />
          <div className="hpmonster-wraper">
            <div className="hp-monster"></div>
          </div>
        </div>
      </div>
      <button className="invent">INVENTORY</button>
    </div>
  );
}

export default ArenaPage;
