import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { changeName, changePicture } from "../features/users";
import { useRef } from "react";

function IndexPage() {
  const username = useSelector((state) => state.users.value.username);
  const dispatch = useDispatch();
  let inImage = useRef();

  function changeUserName() {
    const newName = "Jonas";
    dispatch(changeName(newName));
  }
  function changeUserPicture() {
    const newPic = inImage.current.value;
    dispatch(changePicture(newPic));
  }

  return (
    <div>
      <h1>Index Page</h1>
      <h1>Username: {username}</h1>
      <h2>Email:{useSelector((state) => state.users.value.useremail)}</h2>
      <h2>City:{useSelector((state) => state.users.value.usercity)}</h2>
      <img src={useSelector((state) => state.users.value.userimage)} alt="" />

      <button onClick={changeUserName}>Change Name</button>
      <input type="url" ref={inImage} />
      <button onClick={changeUserPicture}>Change picture</button>
    </div>
  );
}

export default IndexPage;
