import { createSlice } from "@reduxjs/toolkit";

export const productSlice = createSlice({
  name: "products",
  initialState: {
    value: {
      products: [],
    },
  },
  reducers: {
    addProduct: (state, action) => {
      state.value.products = [...state.value.products, action.payload];
    },
    addQuant: (state, action) => {
      state.value.products[action.payload].quant += 1;
    },
    minusQuant: (state, action) => {
      state.value.products[action.payload].quant -= 1;
    },
  },
});
export const { addProduct, addQuant, minusQuant } = productSlice.actions;

export default productSlice.reducer;
