import React from "react";
import { useState } from "react";

function Comp2() {
  const [coloras, setColoras] = useState();
  function showColor() {
    const options = {
      method: "GET",
      headers: {
        "content-type": "application/json",
      },

      credentials: "include",
    };

    fetch("http://localhost:4000/session2", options)
      .then((res) => res.json())
      .then((data) => {
        setColoras(data.col);
        console.log(data.col);
      });
  }
  return (
    <div>
      <div className="colorbox" style={{ backgroundColor: { coloras } }}></div>
      <button onClick={showColor}>ShowColor</button>
    </div>
  );
}

export default Comp2;
