import React from "react";
import { useRef } from "react";
import Picture from "./Picture";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function Comp1() {
  const {
    setComp1,
    setHeader,
    setSidebar,
    sidebar,
    setImage,
    getImage,
    main,
    setMain,
    setmainBar,
    mainBar,
  } = useContext(mainContext);
  // let bgColor = {
  //   flexDirection: togle ? "column" : "row",
  //   flexWrap: togle ? "" : "wrap",
  // };

  const inColor = useRef();
  const inPadding = useRef();
  const inLink1 = useRef();
  const inLink2 = useRef();
  const inLink3 = useRef();

  const inHeaderUrl = useRef();
  const inHeaderText = useRef();
  const inHeaderHight = useRef();

  const inImage = useRef();
  const inTitle = useRef();

  const inMainCheckbox = useRef();
  const inMainColor = useRef();

  function addParam() {
    const value = {
      bgcolor: inColor.current.value,
      padding: inPadding.current.value,
      link1: inLink1.current.value,
      link2: inLink2.current.value,
      link3: inLink3.current.value,
    };
    setComp1(value);
  }
  function setHeaderParam() {
    const headerValue = {
      image: inHeaderUrl.current.value,
      text: inHeaderText.current.value,
      height: inHeaderHight.current.value,
    };
    setHeader(headerValue);
  }
  function sidebarOnOff() {
    setSidebar(!sidebar);
  }

  function addImage() {
    const inputas = {
      image: inImage.current.value,
      title: inTitle.current.value,
    };
    setImage([...getImage, inputas]);
  }
  function changeDirection() {
    setmainBar(!mainBar);
  }

  function setMainFunction() {
    const inputas = {
      color: inMainColor.current.value,
    };
    setMain([...main, inputas]);
  }

  return (
    <div className="comp1">
      <div className="tollbar_set">
        <input ref={inColor} type="text" placeholder="toolbar color" />
        <input ref={inPadding} type="number" placeholder="toolbar padding" />
        <input ref={inLink1} type="text" placeholder="URL for link1" />
        <input ref={inLink2} type="text" placeholder="URL for link2" />
        <input ref={inLink3} type="text" placeholder="URL for link3" />
        <button onClick={addParam}>CONFIRM</button>
      </div>

      <div className="header_set">
        <input ref={inHeaderUrl} type="url" placeholder="header bg image" />
        <input ref={inHeaderText} type="text" placeholder="header text" />
        <input ref={inHeaderHight} type="number" placeholder="header hight" />
        <button onClick={setHeaderParam}>Confirm</button>
      </div>

      <div className="disebar_set">
        <input type="checkbox" onChange={sidebarOnOff} value="cheked" />
        <label> sidebar On / OFF</label>
      </div>

      <div className="addpost">
        <input ref={inImage} type="url" placeholder="post img" />
        <input ref={inTitle} type="text" placeholder="image text" />
        <button onClick={addImage}>ADD</button>
      </div>
      <div className="maincontent">
        <input
          type="checkbox"
          ref={inMainCheckbox}
          onChange={changeDirection}
        />
        <label> row/column</label>
        <input ref={inMainColor} type="text" placeholder="main bg color" />
        <button onClick={setMainFunction}>CONFIRM</button>
      </div>
    </div>
  );
}

export default Comp1;
