import { createSlice } from "@reduxjs/toolkit";

export const userSlice = createSlice({
  name: "users",
  initialState: {
    value: [
      {
        username: "Andrius",
        useremail: "sa@as.lt",
        usercity: "Trakai",
        gender: "Male",
        userimage:
          "https://www.cameraegg.org/wp-content/uploads/2013/03/Canon-EOS-100D-Rebel-SL1-Sample-Image.jpg",
      },
      {
        username: "tomas",
        useremail: "sa@as.lv",
        usercity: "VIlnius",
        userimage:
          "https://www.cameraegg.org/wp-content/uploads/2013/03/Canon-EOS-100D-Rebel-SL1-Sample-Image.jpg",
      },
    ],
  },
  reducers: {
    changeName: (state, action) => {
      state.value.username = action.payload;
    },
    changePicture: (state, action) => {
      state.value.userimage = action.payload;
    },
    addUser: (state, action) => {
      state.value.push(action.payload);
    },
    updateArr: (state, action) => {
      state.value = action.payload;
    },
  },
});
export const { changeName, changePicture, addUser, updateArr } =
  userSlice.actions;

export default userSlice.reducer;
