import React from "react";
import { useState } from "react";
import Question from "../components/Question";

function GamePage() {
  const [gameState, setGameState] = useState("start");
  const [question, setQuestion] = useState(null);
  const [points, setPoints] = useState(0);
  function startGame() {
    fetch("http://localhost:4000/start")
      .then((res) => res.json())
      .then((data) => {
        setQuestion(data.question);
        setGameState("game");
      });
  }

  function answear(correctIndex) {
    console.log(correctIndex);
    fetch("http://localhost:4000/play/" + correctIndex)
      .then((res) => res.json())
      .then((data) => {
        if (!data.end) {
          setQuestion(data.question);
        } else {
          setGameState("end");
          setPoints(data.points);
        }
      });
  }

  return (
    <div className="start">
      {gameState === "start" && <button onClick={startGame}>START GAME</button>}
      {gameState === "game" && <Question item={question} answear={answear} />}
      {gameState === "end" && <h1>GAME OVER. YOU GOT {points}</h1>}
    </div>
  );
}

export default GamePage;
