import { useState, useRef, useEffect } from "react";

const Tasks = ({ getTaskas }) => {
  const [getStatus, setStatus] = useState(getTaskas.status);
  function completed() {
    setStatus(true);
  }
  function deleting() {
    console.log(getStatus);
  }

  return (
    <div
      className="vienastask"
      style={{
        backgroundColor: getStatus ? "green" : "",
      }}
    >
      <div>
        <h5>{getTaskas.date}</h5>
        <h2>{getTaskas.value}</h2>
      </div>
      <div className="buttons">
        <button onClick={completed}>COMPLETED</button>
        <button onClick={completed}>DELETE</button>
      </div>
    </div>
  );
};

export default Tasks;
