import { useState, useRef, useEffect } from "react";

const Items = ({ item }) => {
  return (
    <div className="item">
      <img src={item.image} alt="" />
      <div className="description">
        <p>Price:{item.price}</p>
        {item.maxDamage ? <p>Max demage: {item.maxDamage}</p> : ""}
        {item.energyPerHit ? <p>Energy per hit: {item.energyPerHit}</p> : ""}
        {item.effects ? <p>Effect: {item.effects}</p> : null}
        {item.effect ? <p>Effect: {item.effect}</p> : null}
        <div>Effect:</div>
      </div>
      <button>BUY</button>
    </div>
  );
};

export default Items;
