import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateArr } from "../features/users";
import SingleUser from "./SingleUser";
import { useState } from "react";

import { useRef } from "react";

function Filter() {
  const useriai = useSelector((state) => state.users.value);
  const inGender = useRef();
  const inHair = useRef();
  const inOld = useRef();
  let saveMainArr = [...useriai];
  const [copy, setCopy] = useState(saveMainArr);
  const dispatch = useDispatch();

  function filtering() {
    let arrayForManipulating = [...useriai];
    if (inGender.current.value) {
      arrayForManipulating = arrayForManipulating.filter(
        (x, i) => x.gender === inGender.current.value
      );
      dispatch(updateArr(arrayForManipulating));
    }

    if (inHair.current.value) {
      arrayForManipulating = arrayForManipulating.filter(
        (x, i) => x.hairColor === inHair.current.value
      );
      dispatch(updateArr(arrayForManipulating));
    }
    if (inOld.current.value) {
      arrayForManipulating = arrayForManipulating.filter(
        (x, i) => x.yearOld >= inOld.current.value
      );
      dispatch(updateArr(arrayForManipulating));
    }
    if (
      !inGender.current.value &&
      !inHair.current.value &&
      !inOld.current.value
    ) {
      console.log("tuscia");
      console.log(saveMainArr);
      dispatch(updateArr(copy));
    }
  }

  return (
    <div>
      <input ref={inGender} type="text" placeholder="Filter by gender" />
      <input ref={inHair} type="text" placeholder="Filter by hair color" />
      <input ref={inOld} type="text" placeholder="More than Years Old" />
      <button onClick={filtering}>Filter</button>
    </div>
  );
}

export default Filter;
