import React from "react";
import { useRef } from "react";
import { useSelector, useDispatch } from "react-redux";
import { addUser } from "../features/users";

function NewInput() {
  const dispatch = useDispatch();
  const test = useSelector((state) => state.users.value);
  const inPhoto = useRef();
  const inName = useRef();
  const inLocation = useRef();
  function addToArray() {
    const newUSer = {
      username: inName.current.value,
      usercity: inLocation.current.value,
      userimage: inPhoto.current.value,
    };

    dispatch(addUser(newUSer));

    console.log(test);
  }
  return (
    <div className="inputai">
      <input ref={inPhoto} type="url" name="" id="" placeholder="photo" />
      <input ref={inName} type="text" placeholder="username" />
      <input ref={inLocation} type="text" placeholder="location" />
      <button onClick={addToArray}>ADD</button>
    </div>
  );
}

export default NewInput;
