import React from "react";
import { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function LoginPage() {
  const { getUser, logedinUser, setlogedinUser } = useContext(mainContext);
  const nav = useNavigate();
  let inEmail = useRef();
  let inPass = useRef();
  const [error, setError] = useState(null);

  function verify() {
    const user = getUser.find(({ email }) => email === inEmail.current.value);
    if (
      !user ||
      inPass.current.value === null ||
      inPass.current.value !== user.pass1
    )
      return setError("error");

    setlogedinUser(user);

    nav("/profile/");
  }
  return (
    <div className="login">
      <input ref={inEmail} type="text" placeholder="username" />
      <input ref={inPass} type="text" placeholder="password" />
      {error && <div>NO USER OR WRONG PASS</div>}
      <button onClick={verify}>LOGIN</button>
    </div>
  );
}

export default LoginPage;
