  const [values, setValues] = useState({
    email: "",
    pass1: "",
    pass2: "",
  });
  const [error, setError] = useState([]);
  const [getUser, setUser] = useState([]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (error.includes(name)) {
      setError(error.filter((e) => e !== name));
    }
    setValues({ ...values, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { email, pass1, pass2 } = values; //can add rest according to your requirement
    const errorList = [];
    const regex =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    if (!username || username.length > 20 || username.length < 3) {
      errorList.push("username");
    }
    if (!email || regex.test(email) === false) {
      errorList.push("email");
    }
    if (!pass1 || pass1.length > 20 || pass1.length < 3) {
      errorList.push("pass1");
    }
    if (!pass2 || pass2 !== pass1) {
      errorList.push("pass2");
    }
    if (!location) {
      errorList.push("location");
    }

    setError(errorList);

    console.log(errorList);
    if (errorList.length === 0) {
      console.log("tuscia");
      setUser([...getUser, values]);
    }

    console.log(getUser);
  };
  // console.log(error);
  return (
    <div className="register">
      <input ref={inEmail} type="text" placeholder="enter email" />
      <input ref={inPass1} type="text" placeholder="enter password" />
      <input ref={inPass2} type="text" placeholder="confirm password" />
      <button onClick={addUser}>REGISTER</button>
    </div>
  );
}

export default RegisterPage;
