import React from "react";
import { useContext } from "react";
import mainContext from "../context/mainContext";
import { useRef } from "react";

function Comp4() {
  const inColor = useRef();
  const { color } = useContext(mainContext);
  function setingColor() {
    console.log(inColor.current.value);
  }
  return (
    <div className="blue" style={{ backgroundColor: color[1] }}>
      <input ref={inColor} type="text" />
      <button onClick={setingColor}>SET COLOR</button>
    </div>
  );
}

export default Comp4;
