import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import mainContext from "../context/mainContext";
import { checkLength } from "../features/products";

import { useSelector, useDispatch } from "react-redux";
import { addProduct, addQuant } from "../features/products";

function SingleProductPage() {
  const { setCount } = useContext(mainContext);
  const nav = useNavigate();
  const prod = useSelector((state) => state.products.value.products);

  const dispatch = useDispatch();
  const { id } = useParams();
  const [getSinglePr, setSinglePr] = useState([]);

  useEffect(() => {
    fetch("https://fakestoreapi.com/products/" + id)
      .then((res) => res.json())
      .then((data) => {
        setSinglePr(data);
      });
  }, []);

  function addToCart() {
    if (prod.length === 0) {
      const newProd = { ...getSinglePr };
      newProd.quant = 1;

      dispatch(addProduct(newProd));
      console.log("tuscias array");

      nav("/");
    } else {
      //   console.log(getSinglePr.id);
      prod.forEach((x, i) => {
        if (x.id === getSinglePr.id) {
          //   console.log("jua toks yra");
          dispatch(addQuant(i));
          nav("/");

          return;
        } else {
          const newProd = { ...getSinglePr };
          newProd.quant = 1;

          dispatch(addProduct(newProd));

          nav("/");
        }
      });
    }
    console.log(prod);
    // prod.filte((x,i) => {
    //   if (x.id !== getSinglePr.id) {
    //     const newProd = { ...getSinglePr };
    //     newProd.quant = 1;

    //     dispatch(addProduct(newProd));

    //     nav("/");
    //   }
    // });
  }

  return (
    <div className="productPage">
      <img src={getSinglePr.image} alt="" />
      <div className="productinfo">
        <div>Title{getSinglePr.title}</div>
        <div>Description:{getSinglePr.description}</div>
        <div>Price:{getSinglePr.price}</div>
        <button onClick={addToCart}>ADD to CART</button>
      </div>
    </div>
  );
}

export default SingleProductPage;
