import React from "react";
import { useRef } from "react";
import { useNavigate } from "react-router-dom";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function CreatePost() {
  const { posts, setPosts } = useContext(mainContext);
  const inTitle = useRef();
  const inImage = useRef();
  const inLocation = useRef();
  const nav = useNavigate();

  function addPost() {
    const item = {
      id: Date.now(),
      title: inTitle.current.value,
      image: inImage.current.value,
      location: inLocation.current.value,
    };

    setPosts([...posts, item]);
    nav("/allpost");
    console.log(posts);
  }
  return (
    <div className="createpost">
      <input ref={inTitle} type="text" placeholder="title" />
      <input ref={inImage} type="url" placeholder="image" />
      <input ref={inLocation} type="text" placeholder="location" />
      <button onClick={addPost}>CREATE POST</button>
    </div>
  );
}

export default CreatePost;
