import "./App.css";

import { useState, useRef } from "react";

function App() {
  let inText = useRef();
  let inPhoto = useRef();

  const [getMessage, setMessage] = useState([]);

  function addMessage() {
    if (inText.current.value == "") return alert("ivestike message");

    const message = {
      text: inText.current.value,
      photo: inPhoto.current.value,
    };
    setMessage([...getMessage, message]);
  }

  return (
    <div className="App">
      <div className="container">
        <div className="display">
          {getMessage.map((x) => (
            <div className="objectas">
              {inPhoto.current.value ? <img src={x.photo} alt="" /> : null}
              {/* <img src={x.photo} alt="" /> */}
              <div>{x.text}</div>
            </div>
          ))}
        </div>
        <div className="form">
          <input ref={inPhoto} type="url" placeholder="photo url" />
          <input
            className="message"
            ref={inText}
            type="text"
            placeholder="username"
          />
          <button onClick={addMessage}>SEND</button>
        </div>
      </div>
    </div>
  );
}

export default App;
