import { useState, useEffect } from "react";

const ProgresWM = ({ users }) => {
  console.log(users.length);

  const [getWoman, setWoman] = useState(0);
  const [getMan, setMan] = useState(0);

  useEffect(() => {
    let counter = 0;
    users.map((x) => {
      if (x.gender === "Female") counter++;
    });
    setWoman(counter);
    setMan(users.length - counter);
  }, [users]);

  console.log(getWoman, getMan);

  return (
    <div className="WM_container">
      <h1>Woman:{getWoman}</h1>
      <div
        className="woman"
        style={{ width: (getWoman * 100) / users.length + "%" }}
      ></div>
      <h1>Man:{getMan}</h1>
      <div
        className="man"
        style={{ width: (getMan * 100) / users.length + "%" }}
      ></div>
    </div>
  );
};

export default ProgresWM;
