import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect, useContext } from "react";
import { useNavigate } from "react-router-dom";
import mainContext from "../context/mainContext";
import { checkLength } from "../features/products";

import { useSelector, useDispatch } from "react-redux";
import { addProduct } from "../features/products";

function SingleProductPage() {
  const { setCount } = useContext(mainContext);
  const nav = useNavigate();
  const prod = useSelector((state) => state.products.value);

  const dispatch = useDispatch();
  const { id } = useParams();
  const [getSinglePr, setSinglePr] = useState([]);

  useEffect(() => {
    fetch("https://fakestoreapi.com/products/" + id)
      .then((res) => res.json())
      .then((data) => {
        setSinglePr(data);
      });
  }, []);

  function addToCart() {
    const newProd = {
      product: getSinglePr,
      quantity: 1,
    };
    dispatch(addProduct(newProd));

    nav("/");
    console.log(prod);
  }

  return (
    <div className="productPage">
      <img src={getSinglePr.image} alt="" />
      <div className="productinfo">
        <div>Title{getSinglePr.title}</div>
        <div>Description:{getSinglePr.description}</div>
        <div>Price:{getSinglePr.price}</div>
        <button onClick={addToCart}>ADD to CART</button>
      </div>
    </div>
  );
}

export default SingleProductPage;
