import React from "react";
import { useRef } from "react";
import Picture from "./Picture";
import { useContext } from "react";
import mainContext from "../context/mainContext";

function Comp1() {
  const { setComp1 } = useContext(mainContext);
  // let bgColor = {
  //   flexDirection: togle ? "column" : "row",
  //   flexWrap: togle ? "" : "wrap",
  // };

  const inColor = useRef();
  const inPadding = useRef();
  const inLink1 = useRef();
  const inLink2 = useRef();
  const inLink3 = useRef();

  const inHeaderUrl = useRef();
  const inHeaderText = useRef();
  const inHeaderHight = useRef();

  function addParam() {
    const value = {
      bgcolor: inColor.current.value,
      padding: inPadding.current.value,
      link1: inLink1.current.value,
      link2: inLink2.current.value,
      link3: inLink3.current.value,
    };
    setComp1(value);
  }
  function setHeaderParam() {}

  return (
    <div className="comp1">
      <div className="tollbar_set">
        <input ref={inColor} type="text" placeholder="toolbar color" />
        <input ref={inPadding} type="number" placeholder="toolbar padding" />
        <input ref={inLink1} type="text" placeholder="URL for link1" />
        <input ref={inLink2} type="text" placeholder="URL for link2" />
        <input ref={inLink3} type="text" placeholder="URL for link3" />
        <button onClick={addParam}>CONFIRM</button>
      </div>

      <div className="header_set">
        <input ref={inHeaderUrl} type="url" placeholder="header bg image" />
        <input ref={inHeaderText} type="text" placeholder="header text" />
        <input ref={inHeaderHight} type="number" placeholder="header hight" />
        <button>Confirm</button>
      </div>

      <div className="disebar_set">
        <input type="checkbox" />
        <label> sidebar On / OFF</label>
      </div>

      <div className="addpost">
        <input type="url" placeholder="post img" />
        <input type="text" placeholder="image text" />
      </div>
      <div className="maincontent">
        <input type="checkbox" />
        <label> row/column</label>
        <input type="text" placeholder="main bg color" />
      </div>
    </div>
  );
}

export default Comp1;
